import { hasBasename, stripBasename, stripTrailingSlash, addLeadingSlash, stripLeadingSlash, stripPrefix, parsePath, createPath } from '../path-utils';
describe('path-utils', function () {
    describe('hasBasename', function () {
        it('should return true when is basename', function () {
            var resp = hasBasename('/base/path', '/base');
            expect(resp).toBeTruthy();
        });
        it('should return false when is not a  basename', function () {
            var resp = hasBasename('/base/path', '/src');
            expect(resp).toBeFalsy();
        });
    });
    describe('stripBasename', function () {
        it('should strip if basename supplied is basename', function () {
            var resp = stripBasename('/base/path', '/base');
            expect(resp).toEqual('/path');
        });
        it('should not strip if basename supplied it is not a basename', function () {
            var resp = stripBasename('/base/path', '/');
            expect(resp).toEqual('/base/path');
        });
    });
    describe('stripTrailingSlash', function () {
        it('should strip a trailing slash', function () {
            var resp = stripTrailingSlash('/base/path/');
            expect(resp).toEqual('/base/path');
        });
        it('should not strip last char if there is no trailing slash', function () {
            var resp = stripTrailingSlash('/base/path');
            expect(resp).toEqual('/base/path');
        });
    });
    describe('addLeadingSlash', function () {
        it('should add a first slash', function () {
            var resp = addLeadingSlash('base/path');
            expect(resp).toEqual('/base/path');
        });
        it('should not add a first slash if string already has one', function () {
            var resp = addLeadingSlash('/base/path');
            expect(resp).toEqual('/base/path');
        });
    });
    describe('stripLeadingSlash', function () {
        it('should strip first slash', function () {
            var resp = stripLeadingSlash('/base/path');
            expect(resp).toEqual('base/path');
        });
        it('should not strip first char is not a slash', function () {
            var resp = stripLeadingSlash('base/path');
            expect(resp).toEqual('base/path');
        });
    });
    describe('stripPrefix', function () {
        it('should strip prefix', function () {
            var resp = stripPrefix('/base/path', '/ba');
            expect(resp).toEqual('se/path');
        });
        it('should not strip if does not begin with prefix', function () {
            var resp = stripPrefix('/base/path', 'bas');
            expect(resp).toEqual('/base/path');
        });
    });
    describe('parsePath', function () {
        it('should parse a simple url path', function () {
            var resp = parsePath('/base/path');
            expect(resp).toEqual({
                pathname: '/base/path',
                search: '',
                hash: ''
            });
        });
        it('should parse a simple url path with search', function () {
            var resp = parsePath('/base/path?a=1&b=2');
            expect(resp).toEqual({
                pathname: '/base/path',
                search: '?a=1&b=2',
                hash: ''
            });
        });
        it('should parse a simple url path with hash', function () {
            var resp = parsePath('/base/path#blue');
            expect(resp).toEqual({
                pathname: '/base/path',
                search: '',
                hash: '#blue'
            });
        });
        it('should parse a url path with hash and search', function () {
            var resp = parsePath('/base/path?a=1&b=2#blue');
            expect(resp).toEqual({
                pathname: '/base/path',
                search: '?a=1&b=2',
                hash: '#blue'
            });
        });
    });
    describe('createPath', function () {
        it('should parse a simple url path', function () {
            var resp = createPath({
                pathname: '/base/path',
                search: '',
                hash: ''
            });
            expect(resp).toEqual('/base/path');
        });
        it('should parse a simple url path with search', function () {
            var resp = createPath({
                pathname: '/base/path',
                search: '?a=1&b=2',
                hash: ''
            });
            expect(resp).toEqual('/base/path?a=1&b=2');
        });
        it('should parse a simple url path with hash', function () {
            var resp = createPath({
                pathname: '/base/path',
                search: '',
                hash: '#blue'
            });
            expect(resp).toEqual('/base/path#blue');
        });
        it('should parse a url path with hash and search', function () {
            var resp = createPath({
                pathname: '/base/path',
                search: '?a=1&b=2',
                hash: '#blue'
            });
            expect(resp).toEqual('/base/path?a=1&b=2#blue');
        });
    });
});
